import Profile from './pages/Profile'
import Home from './pages/Home'
import './style.css'
import store from './store'
export class app extends PureComponent {
  constructor() {
    super()
  }
  componentDidMount() {
    store.subscribe(() => {
      const state = store.getState()
    })
  }
  render() {
    return (
      <div>
        <h2>App Counter：0 </h2>
        <div className="pages">
          <Home></Home>
          <Profile></Profile>
        </div>
      </div>
    )
  }
}

export default app
