import {
  ADD_NUMBER,
  DEL_NUMBER,
  CHANGE_BANNER,
  CHANGE_RECOMMEND,
} from "./constants";
export const addNumberAction = (num) => ({
  type: ADD_NUMBER,
  num,
});
export const delNumberAction = (num) => ({
  type: DEL_NUMBER,
  num,
});
export const changeBannerAction = (banner) => ({
  type: CHANGE_BANNER,
  banner,
});
export const changeRecommendAction = (recommend) => ({
  type: CHANGE_RECOMMEND,
  recommend,
});
